class Solution {
    public int numberOfAlternatingGroups(int[] c, int k) {
        int n=c.length;
        int[] dp = new int[n];
        dp[0] = 1;
        for(int i=1;i<n;i++){
            if(c[i] != c[i-1]) dp[i] = dp[i-1]+1;
            else dp[i] = 1;
        }
        int ct =0;
        for(int i=0;i<n;i++){
            int chain = 0;
            if(i < k-1){
                chain = dp[i];
                if(chain == i+1 && c[0] != c[n-1]) chain += dp[n-1];
            }else chain = dp[i];
            if(chain >= k) ++ct;
        }
        return ct;
    }
}
