class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors += colors[:k-1]
        n = len(colors)
        dp = [i for i in range(n)]
        for i in range(n - 2, -1, -1):
            if colors[i] != colors[i+1]:
                dp[i] = dp[i+1]
        res = 0
        for i in range(n - k + 1):
            if dp[i] - i + 1 >= k:
                res += 1
        return res
