impl Solution {

    pub fn number_of_alternating_groups(colors: Vec<i32>, k: i32) -> i32 {
        let n = colors.len();
        let mut ans = 0;
        let k = k as usize;
        let mut p = 1;
        for i in 1..n+k-1 {
            if colors[i%n] != colors[(i-1)%n] {
                p += 1;
            } else {
                p = 1;
            }
            if p == k {
                ans += 1;
            }
            p = p.min(k-1);
        }

        ans
    }
}
