class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        for(int i=0; i<k-1; i++)v.push_back(v[i]);
        int l=v.size();
        vector<int> a(l,0);
        for(int i=1; i<l; i++){
            if(v[i]==v[i-1])a[i]=1;
        }
        vector<int> pf=v;
        for(int i=1; i<l; i++){
            a[i]+=a[i-1];
            pf[i]+=pf[i-1];
        }
        int ans=0;
        for(int i=k-1; i<l; i++){
            int sum=0;
            if(v[i]==0)sum=k/2;
            else sum=(k+1)/2;
            if(i==k-1){
                if(pf[i]==sum && a[i]==0)ans++;
            }
            else{
                int x=pf[i]-pf[i-k];
                int y=a[i]-a[i-k+1];
                if(x==sum && y==0)ans++;
            }
        }
        return ans;
    }
};
