class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int count = 0;
        int ap = 0;
        for(int i=0; i<(k-1); ++i) {
            if(colors[i] != colors[i+1]) ap += 1;
        }
        if (ap == k - 1) count += 1;

        for(int i = 1; i < n; ++i) {
            int prevIdx = (i - 1) % n;
            int nextIdx = (i + k - 2) % n;
            if(colors[prevIdx] != colors[(prevIdx + 1) % n]) ap -= 1;
            if(colors[nextIdx] != colors[(nextIdx + 1) % n]) ap += 1;
            if(ap == k - 1) count += 1;
        }
        return count;
    }
};
