class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0,ctr=1,n=colors.size();
        for(int i=0;i<n;i++){
            colors.push_back(colors[i]);
        }
        n=colors.size();
        for(int i=1;i<n;i++){
            if(i-k>=n/2-1) break;
            if(colors[i]!=colors[i-1]) ctr++;
            else ctr=1;
            if(ctr>=k) ans++;
        }
        return ans;
    }
};
