class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0;i<k-1;i++) colors.push_back(colors[i]);
        int n = colors.size();
        int len=0;
        int ans=0;
        for(int i=0;i<n;i++){
            if(len==0) len++;
            else if(colors[i]!=colors[i-1]){
                len++;
            }
            else len=1;
            if(len>=k) ans++;
        }
        return ans;
    }
};
