class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0;i<k-1;i++){
            colors.push_back(colors[i]);
        }
        int ans=0;
        int ct=1;
        colors.push_back(colors.back());
                int n=colors.size();

        for(int i=1;i<n;i++){
            if(colors[i]!=colors[i-1]){
                ct++;
            }
            else{
                if(ct-k+1>=0)
                    ans+=ct-k+1;
                ct=1;
            }
        }
        return ans;
    }
};
