class Solution {
public:
    const static int N = 2e5+5;
    int f[N];
    // int s[N];
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
        f[0] = 0;
        f[1] = 0;
        for(int i=1;i<n;i++){
            f[i] = f[i-1]+(c[i]!=c[i-1]);
        }
        f[n] = f[n-1] +(c[0]!=c[n-1]);
        for(int i=1;i<n;i++){
            f[n+i] = f[n+i-1]+(c[i]!=c[i-1]);
        }
        int cnt=0;
        for(int i=0;i<n;i++){
            if(f[i+n] - f[i+n-k+1] == k-1){
                cnt++;
            }
        }
        return cnt;
    }
};
