class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> data(colors.size());
        data[0] = 1;
        int result = 0;
        size_t left = colors.size();
        size_t right = colors.size();
        for(size_t i=1;i<data.size();++i) {
            if(colors[i] == colors[i-1]) {
                data[i] = 1;
                if(left == colors.size()) {
                    left = i-1;
                }
                right = i;
                continue;
            }
            data[i] = data[i-1] + 1;
            if(data[i] >= k) {
                result++;
            }
        }
        if(colors[0] == colors[data.size() - 1]) {
            return result;
        }
        if(left == colors.size()) {
            return result + k - 1;
        }
        left = min(left,(size_t)k-2);
        right = max(right,data.size() + 1 - k);
        if(left + 2 + data.size() - right < k) {
            return result;
        }
        return result + left + 2 + data.size() - k - right;
    }
};
