class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        int n = colors.size();

        for(int i=0; i<k-1; i++)
            colors.push_back(colors[i]);

        n = colors.size();

        int prev = colors[0];

        int curr = 1;

        for(int i=1; i<n; i++)
        {
            if(colors[i] == prev)
            {
                curr = 1;
            }

            else
            {
                curr++;

                if(curr == k)
                {
                    ans++;
                    curr--;
                }
            }

            prev = colors[i];
        }

        return ans;

    }
};
