package main

import (
	"fmt"
)

func numberOfAlternatingGroups(colors []int, k int) int {
	n := len(colors)

	// Duplicate the colors slice to handle circular checking
	extendedColors := make([]int, n+k)
	copy(extendedColors, colors)
	copy(extendedColors[n:], colors[:k])

	result := countAlternatingGroups(extendedColors, n, k)

	return result
}

func countAlternatingGroups(colors []int, n, k int) int {
	start := 1
	end := 0
	result := 0

	for end < n && start-k < n {
		if colors[start] != colors[start-1] {
			start++
		} else {
			end = start
			start++
		}

		count := 0
		if start-end >= k {
			count++
			end++
		}
		result += count
	}

	return result
}
