class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        for (int i=0; i<n+k-1;) {
            int j = i+1;
            while (j<n+k-1 && colors[j%n] != colors[(j-1+n)%n]) j++;
            ans += max(j-i-k+1, 0);
            // cout << ans << "<-ans ";
            // cout << j << endl;
            i = j;
        }
        return ans;
    }
};
