function cacheFn<T, K extends unknown[]>(fn: (...args: K) => T) {
  const cache: Map<string, T> = new Map();
  return function (...args: K) {
    const key = args.toString();
    if (!cache.has(key)) {
      cache.set(key, fn(...args));
    }
    return cache.get(key) as T;
  }
}
function numberOfAlternatingGroups(colors: number[], k: number): number {
  let ans = 0;
  if (colors.every((color, idx) => color !== colors[(idx - 1 + colors.length) % colors.length])) {
    return colors.length;
  }
  const calculate = cacheFn((idx: number): number => {
    if (colors[idx] !== colors[(idx - 1 + colors.length) % colors.length]) {
      return calculate((idx - 1 + colors.length) % colors.length) + 1;
    }
    return 1;
  });
  for (let i = 0; i < colors.length; i++) {
    if (colors[i] !== colors[(i - 1 + colors.length) % colors.length] && calculate((i - 1 + colors.length) % colors.length) >= k - 1) {
      ans++;
    }
  }
  return ans;
};
