class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int cnt=2,i=0,j=0;
        vector<int>vec;
        while(cnt--){
            for(int i=0;i<colors.size();i++) vec.push_back(colors[i]);
        }
        cnt=0;
        while(i<vec.size() && j<colors.size()){
            if(i-j+1==k){
                cnt++;
                j++;
            }
            if(i+1<vec.size() && vec[i]==vec[i+1]) j=i+1;
            i++;
        }
        return cnt;
    }
};
