class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {
          int n = nums.size();
        int ans = 0;
        vector<int> color = nums;

        color.insert(color.end(), nums.begin(), nums.begin() + k - 1);

        bool res = true;
        for (int i = 0; i < k - 1; ++i) {
            if (color[i] == color[i + 1]) {
                res = false;
                break;
            }
        }

        if (res) ans++;
        int i=1;
        while(i<n) {
            if (color[i + k - 2] == color[i + k - 1]) {
                res = false;
            }
            if (color[i - 1] == color[i]) {
                res = true;
                for (int j = i; j < i + k - 1; ++j) {
                    if (color[j] == color[j + 1]) {
                        res = false;
                        break;
                    }
                }
            }
            if (res) ans++;
            i++;
        }

     return ans;


    }
};
