class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k) -> int:
        for i in range(k - 1):
            colors.append(colors[i])

        p = 1
        res = 0
        for i in range(1, len(colors)):
            if colors[i] != colors[i - 1]:
                p += 1
            else:
                p = 1
            if p >= k:
                res += 1
        return res
