class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();

        int prev = -1;
        int cnt = 0;
        int res = 0;
        for (int i = 0; i <= n + k - 2; ++i)
        {
            int t = i % n;
            if (prev == 0 && colors[t] == 1) ++cnt;
            else if (prev == 1 && colors[t] == 0) ++cnt;
            else
            {
                cnt = 1;
            }

            prev = colors[t];
            if (cnt >= k) ++res;
            //cout << i << "," << k << "," << res << endl;
        }

        return res;
    }
};
