class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        if k > n:
            return 0

        same = 0
        for i in range(k - 1):
            if colors[i] == colors[i + 1]:
                same += 1

        count = 0
        if same == 0:
            count += 1

        for i in range(1, n):

            if colors[i - 1] == colors[(i - 1 + 1) % n]:
                same -= 1
            if colors[(i + k - 1) % n] == colors[(i + k - 2) % n]:
                same += 1

            if same == 0:
                count += 1

        return count
