class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = int(colors.size()), ans = 0, po = 0;
        for (int i = 0; i < k - 1; i++){
            colors.push_back(colors[i]);
        }
        n = int(colors.size());

        for (int i = 1; i < n; i++){
            if (colors[i] == colors[i - 1]) po = i;
            if (i - po >= k - 1) ans++;
        }

        return ans;
    }
};
