class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        int n = colors.size();
        int prev = -1;
        int cnt = 0;
        for(int i=0;i<n+k-1;i++){
            if(colors[i%n]==prev){
                ans += max(0,cnt-k+1);
                cnt = 1;
            }
            else cnt++;

            prev = colors[i%n];
        }
        ans += max(0,cnt-k+1);
        return ans;
    }
};
