#define ll long long
#define loopf(i, a, b) for (int i = a; i < b; i++)

class Solution
{
public:
    int numberOfAlternatingGroups(vector<int> &v, int k)
    {
        int n = v.size();
        vector<int> t(2 * n);

        loopf(i, 0, 2 * n)
        {
            t[i] = v[i % n];
        }

        vector<int> state;

        loopf(i, 0, 2*n-1)
        {
            state.push_back(t[i] != t[i + 1]);
        }

        // for(auto ele : state)
        // {
        //     cout << ele << " ";
        // }
        // cout << endl;

        int ans = 0;
        int curr = 0;

        loopf(i, 0, k - 2)
            curr += state[i];

        loopf(i, 0, n)
        {
            curr += (state[i + k - 2]);
            ans += (curr == k - 1);
            curr -= state[i];
        }

        return ans;
    }
};
