class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
        for (int i = 0; i < n; i++) {
            c.push_back(c[i]);
        }
        vector<int> p = {0};
        for (int i = 1; i < 2 * n; i++) {
            if (c[i] != c[i - 1]) {
                p.push_back(1);
            } else {
                p.push_back(0);
            }
        }
        for (int i = 1; i < p.size(); i++) {
            p[i] += p[i - 1];
        }
        int res = 0;
        for (int i = 0; i < n; i++) {
            int l = i, r = i + k - 2;
            if (p[r + 1] - p[l] == k - 1) {
                res++;
            }
        }
        return res;
    }
};
