class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        if (k > n) {
            return 0;
        }
        int same = 0;
        for (int i = 0; i < k - 1; i++) {
            if (colors[i] == colors[i + 1]) {
                same++;
            }
        }
        int count = 0;
        if (same == 0) {
            count++;
        }
        for (int i = 1; i < n; i++) {
            if (colors[i - 1] == colors[(i - 1 + 1) % n]) {
                same--;
            }
            if (colors[(i + k - 1) % n] == colors[(i + k - 2) % n]) {
                same++;
            }

            if (same == 0) {
                count++;
            }
        }

        return count;
    }
}
