from itertools import *
from functools import *
from collections import *
class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        X = colors + colors
        N = len(colors)
        Y = [0]*(2*N)
        for i in range(2*N-1):
            if X[i]!=X[i+1]:
                Y[i] = 1
        SY = list(accumulate([0]+Y))
        ans = 0
        for i in range(N):
            if SY[i+k-1]-SY[i]==k-1:
                ans += 1
        # print(Y)
        return ans
