class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        int ans=0,cnt=0;
        for(int i=0;i<n;i++) a.push_back(a[i]);
        for(int i=1,j=1;i<2*n-1;i++)
        {
            if(a[i]!=a[i-1]&&a[i]!=a[i+1]) 
            {
                cnt++;
            }
            if(i>=k-2) 
            {
                if(cnt==k-2&&j<=n) ans++;
                if(a[j]!=a[j-1]&&a[j]!=a[j+1]) cnt--;
                j++;
            }
        }
        return ans;
    }
};
