class Solution {
public:
     int solution(const vector<int>& colors, int k) {
        int f = 0;
        int i = 0;
        int j = 1;

        while (j < colors.size() + k && i < colors.size()) {
            int tj = j;
            int te = tj % colors.size(); 

            while (colors[te] != colors[(te - 1 + colors.size()) % colors.size()] && 
                tj < (colors.size() + k - 1)) {
                tj += 1;
                te = tj % colors.size();
            }

            f += max(tj - i - k + 1, 0);
            i = tj;
            j = i + 1;
        }

        return f;
    }

    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=solution(colors,k);
        return ans;
    }
};
