class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int cnt = 1;

        int n = v.size();

        int pre = v[0];

        int tot = n + k -1;

        int ans = 0;

        for(int i=1;i<tot;i++){

            int cur = i%n;

            if(pre!=v[cur]){
                cnt++;
            }
            else{
                cnt=1;
            }

            if(cnt>=k) ans++;

            pre = v[cur];

        }
        // cout<<"\n";
        return ans;
    }
};
