class Solution:
    def numberOfAlternatingGroups(self, a: List[int], k: int) -> int:
        a += a[:k - 1]
        pre = -1
        c = 0
        res = 0
        for x in a:
            if x != pre:
                c += 1
            else:
                res += max(0, c - k + 1)
                c = 1
            pre = x
        res += max(0, c - k + 1)
        return res
