class Solution {
public:
    int S[100005];

    bool yes(int x, int y){
        int s = S[y];
        if (x - 1 >= 0) s -= S[x - 1];
        return s == y - x + 1;
    }

    int numberOfAlternatingGroups(vector<int>& a, int K) {
        int f[100005], i, j, res = 0, l, r, n = a.size();
        for (i = 0; i < n; ++i){
            l = i - 1;
            if (l < 0) l += n;
            r = i + 1;
            if (r >= n) r -= n;
            if (a[i] != a[l] && a[i] != a[r]) f[i] = 1;
            else f[i] = 0;
        }

        S[0] = f[0];
        for (i = 1; i < n; ++i) S[i] = S[i - 1] + f[i];
        for (i = 0; i < n; ++i){
            l = i + 1;
            if (l >= n) l -= n;
            r = i + (K - 2);
            if (r >= n) r -= n;
            if (l <= r && yes(l, r)) ++res;
            if (l > r && yes(l, n - 1) && yes(0, r)) ++res;
        }

        return res;
    }
};
