class Solution:
    def heapify(arr, n, i):
        largest = i
        l = 2 * i + 1
        r = 2 * i + 2

        if l < n and arr[l] > arr[largest]:
            largest = l

        if r < n and arr[r] > arr[largest]:
            largest = r

        if largest != i:
            arr[i], arr[largest] = arr[largest], arr[i]
            heapify(arr, n, largest)

    def heapSort(arr):
        n = len(arr)
        for i in range(n // 2 - 1, -1, -1):
            heapify(arr, n, i)

        for i in range(n - 1, 0, -1):
            arr[0], arr[i] = arr[i], arr[0]
            heapify(arr, i, 0)
    def numberOfAlternatingGroups(self, arr: List[int], k: int) -> int:
        n = len(arr)
        if k == 1:
            return n
        count = 0
        count1 = 0

        for i in range(k - 1):
            if arr[i] != arr[i + 1]:
                count1 += 1

        if count1 == k - 1:
            count += 1

        for i in range(1, n):
            if arr[(i - 1) % n] != arr[(i - 1 + 1) % n]:
                count1 -= 1
            if arr[(i + k - 2) % n] != arr[(i + k - 1) % n]:
                count1 += 1
            if count1 == k - 1:
                count += 1

        return count


    def partition(arr, start, end):
        index = 0
        pivotElement = arr[end]
        pivotIndex = 0
        temp = [0] * (end - start + 1)

        for i in range(start, end + 1):
            if arr[i] < pivotElement:
                temp[index] = arr[i]
                index += 1

        temp[index] = pivotElement
        index += 1

        for i in range(start, end):
            if arr[i] > pivotElement:
                temp[index] = arr[i]
                index += 1

        index = 0
        for i in range(start, end + 1):
            if arr[i] == pivotElement:
                pivotIndex = i
            arr[i] = temp[index]
            index += 1

        return pivotIndex
    def quickSort(arr, start, end):
        if start < end:
            partitionIndex = partition(arr, start, end)
            quickSort(arr, start, partitionIndex - 1)
            quickSort(arr, partitionIndex + 1, end)

    def getMax(arr):
        mx = arr[0]
        for i in range(1, len(arr)):
            if arr[i] > mx:
                mx = arr[i]
        return mx

    def countSort(arr, exp):
        n = len(arr)
        output = [0] * n
        count = [0] * 10

        for i in range(n):
            count[(arr[i] // exp) % 10] += 1

        for i in range(1, 10):
            count[i] += count[i - 1]

        for i in range(n - 1, -1, -1):
            output[count[(arr[i] // exp) % 10] - 1] = arr[i]
            count[(arr[i] // exp) % 10] -= 1

        for i in range(n):
            arr[i] = output[i]
