class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {
        int n = nums.size();
        nums.insert(nums.end(), nums.begin(), nums.end());
        int res = 0;
        for(int i = 1, j = 0; i < n + k - 1; ++i) {
            if(i == j) continue;
            if(nums[i] != nums[i - 1]) {
                if(i - j + 1 == k) {
                    res++;
                    j++;
                }
            } else {
                j = i;
            }
        }
        return res;
    }
};
