class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k)
    {
        int found = -1, n = v.size(), ret = 0;
        for(int i = 0 ; i < n ; i++)
        {
            if((v[i]==v[(i+1)%n])){
                found = i;
                break;
            }
        }
        if(found==-1) return n;
        vector<int> vn;
        for(int i = found+1 ; i < n ; i++)
            vn.push_back(v[i]);
        for(int i = 0 ; i <= found ; i++)
                vn.push_back(v[i]);
        v = vn;
        int  i  = 0;
        while(i<n)
        {
            int key = v[i];
            int cnt = 0;
            while(i<n && v[i]==key)
            {
                i++,cnt++,key = 1-key;
            }
            cnt = cnt-k+1;
            cnt = max(cnt, 0);
            ret += cnt;
        }
        return ret;
    }
};
