class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for (int i = 0; i < k - 1; ++i) colors.push_back(colors[i]);
        int window = 0, res = 0;

        for (int i = 0; i < colors.size(); ++i) {
            if (i > 0) window += colors[i] != colors[i - 1];
            if (i >= k) window -= colors[i - k + 1] != colors[i - k];
            if (i >= k - 1) res += window == k - 1;
        }

        return res;
    }
};
