class Solution {
public:
   int numberOfAlternatingGroups(vector<int>& C, int k) {
    vector<int> exC = C;
    exC.insert(exC.end(), C.begin(), C.begin() + k - 1);
    int x = 0, y = 1;
    for (int i = 1; i < exC.size(); ++i) {
        if (exC[i] != exC[i - 1]) {
            ++y;
        } else {
            y = 1;
        }
        if (y >= k) {
            ++x;
        }
    }
    return x;

    }
};
