class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        int l = 0, r = 0;
        int len = colors.size();
        while(l < len){
            while(l < len && r - l + 1 < k){
                r++;
                if(colors[r % len] == colors[(r + len - 1) % len]){
                    l = r;
                }
            }
            if(l >= len)    break;
            //cout << l << ' ' << r << '\n';
            ans++;
            l++;
        }
        return ans;
    }
};
