class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n = v.size();
        for(int i=0 ; i<n ; i++){
            v.push_back(v[i]);
        }
        int l = 0 ;
        int ans =0;
        for(int i=1 ; i<n + k - 1 ;i++){
            if(v[i] == v[i-1]){
                l = i;
            }else if(i-l+1 >= k){
                ans++;
            }
        }
        return ans;
    }
};
