class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int res=0;
        int n=colors.size();
        vector<int> count(n);

        for (int i=0; i<n; ++i) {
            if (colors[i] != colors[(i-1+n)%n]) {
                count[i]=1;
                if (i) count[i] += count[i-1];
            }
        }

        for (int i=0; i<n; ++i) {
            int j=i-k;
            if (j>=-2) {
                int temp1=count[i];
                int temp2= j==-2? 0 : count[j+1];
                if (temp1-temp2==k-1) ++res;
            } else {
                int temp1=count[i];
                int temp2=count.back()-count[(j+n+1)%n];

                if (temp1+temp2==k-1) ++res;
            }
        }


        return res;
    }
};
