class Solution { 
public: 
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
        int n = colors.size(); 
        if (k == 1) return n;
        int ans = 0, altL = 1, i = 1; 
        while (i < n + k - 1) { 
            int cur = i, prev = i - 1; 

            if (cur >= n) cur -= n; 
            if (prev >= n) prev -= n; 

            if (colors[cur] != colors[prev]) altL++; 
            else    altL = 1; 

            if (altL >= k) ans++; 
            i++; 
        } 
        return ans; 
    } 
};
