class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for (int i = 0; i < k-1; i++) {
            colors.push_back(colors[i]);
        }

        int len = 1, ans = 0;
        for (int i = 1; i < (int) size(colors); i++) {
            if (colors[i] != colors[i-1]) {
                len++;
            } else {
                ans += max(0, len-k+1);
                len = 1;
            }
        }
        ans += max(0, len-k+1);

        return ans;
    }
};
