class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
  int cnt = 1;
  int res = 0;
  for (int i = 1; i < n + k - 1; i++) {
    int ind = i % n;
    if (colors[ind] != colors[(ind - 1 + n) % n]) {
      cnt++;
    }
    else {
      cnt = 1;
    }
    if (cnt >= k)res++;
  }
  return res;
    }
};
