class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {

        int n = c.size();

        for(int i=0;i<k-1;i++){
            c.push_back(c[i]);
        }

        c.push_back(c[n+k-2]);


        int prev = 0;
        n = c.size();
        int cnt = 0;
        for(int i=0;i<n-1;i++){
            if(c[i] == c[i+1]){
                int idx = i-k+1;

                if(prev<=idx){
                    cnt += idx-prev+1;
                }

                prev = i+1;
            }
        }

        return cnt;






    }
};
