class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int s = 0, ans  = 0;
        for(int i = 1; s< n; i++){
            // cout <<i<<" ";
            if(colors[i%n] == colors[(i-1)%n])
                s = i;
            else if(i -s + 1 == k){
                ans++;
                s++;
            }
        }
        return ans;
    }
};
