class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int[] arr = new int[n * 2];
        for (int i = 0; i < n; i++) {
            arr[i] = colors[i];
            arr[i + n] = colors[i];
        }

        int count = 0;
        int ans = 0;
        for (int i = 0; i < 2 * n; i++) {
            if (i == 0) {
                count = 1;
            } else {
                if (arr[i] != arr[i - 1]) {
                    count++;
                } else {
                    count = 1;
                }
            }
            if (i >= n && count >= k) {
                ans++;
            }
        }
        return ans;
    }
}
