class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int i=1,cnt=1,j=0;
        int res=0;
        while(i<n+k-1){
            while(i<n+k-1 && cnt<k){
                int curr = (i)%n;
                int prev = (i-1)%n;
                if(colors[curr]==colors[prev]){
                    cnt=1;
                    j=i;
                    i++;
                }else{
                    cnt++;
                    // if(cnt==k)break;
                    i++;
                }
            }
            if(cnt==k){
                res++;
                j++;
                cnt--;
            }
        }
        return res;

    }
};
