class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i = 0; i < k - 1; i++) colors.push_back(colors[i]);
        int l = 0, ret = 0;
        for(int i = 1; i < colors.size(); i++){
            if(colors[i] == colors[i - 1]) l = i;
            if(i - l + 1 >= k) ret++;
        }

        return ret;
    }
};
