class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {

        int n = col.size(), ms = 1, ans = 0;


        for(int i=0; i<n + k - 1; i++){

            int last = i-1;

            if( i>0 && col[ i%n ] != col[ last%n ] ){

                ms++;

            }else if(  i > 0 ) {

                ms = 1;

            }

            ans += ( ms >= k ) ? 1 : 0;

        }

        return ans;
    }
};
