class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        nums = [0]*n
        for i,x in enumerate(colors):
            nums[i] = colors[(i-1)]^x
        # print(nums)
        # if sum(nums) == 
        pre = [0]*(n*2)
        for i in range(n*2):
            pre[i] = pre[i-1] + nums[(i)%n]

        ans = 0
        # print(pre)
        for i in range(n):
            # print(i,pre[k+i-1],pre[i],k+i-1,i)
            if pre[k+i-1] - pre[i] == k-1:
                ans += 1
        return ans
