class Solution:
    def numberOfAlternatingGroups(self, a: List[int], k: int) -> int:
        n = len(a)
        a = a + a
        q = deque()
        ans = 0
        for i in range(n + k - 1):
            if q and q[-1] == a[i]:
                q = deque()
            q.append(a[i])
            if len(q) >= k:
                ans += 1
        return ans
