class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int>c(n+k-1);
        int m=n+k-1;
        for(int i=0;i<m;i++){
            c[i]=colors[i%n];
        }
        int dp[m];
        dp[m-1]=1;
        int ans=0;
        for(int i=m-2;i>=0;i--){
            if(c[i]!=c[i+1]){
                dp[i]=1+dp[i+1];
            }else{
                dp[i]=1;
            }
            if(dp[i]>=k) ans++;
        }
        return ans;
    }
};
